import React from 'react';
import './Engtothai.css';
import { useState } from 'react';

function EngtoThai() {
  const [language, setLanguage] = useState('en');

  const words = {
    en: {
      hello: 'Hello World',
      about: 'About Us',
      learn: 'Learn a Language',
      resources: 'Resources',
      contact: 'Contact',
      history: 'History',
      feedback: 'Feedback',
    },
    es: {
      hello: 'Hola Mundo',
      about: 'Sobre Nosotros',
      learn: 'Aprende un Idioma',
      resources: 'Recursos',
      contact: 'Contacto',
      history: 'Historia',
      feedback: 'Comentarios',
    },
    // Add more languages as needed
  };

  const handleLanguageChange = (event) => {
    setLanguage(event.target.value);
  };

  return (
    <div className="App">
      <header className="header">
        <img src="logo.png" alt="App Logo" className="logo" />
        <h1 className="title">{words[language].hello}</h1>
        <nav className="navigation">
          <ul>
            <li>
              <a href="#">{words[language].about}</a>
            </li>
            <li>
              <a href="#">{words[language].learn}</a>
            </li>
            <li>
              <a href="#">{words[language].resources}</a>
            </li>
            <li>
              <a href="#">{words[language].contact}</a>
            </li>
          </ul>
          <div className="language-selector">
            <select value={language} onChange={handleLanguageChange}>
              <option value="en">English</option>
              <option value="es">Spanish</option>
              {/* Add options for more languages */}
            </select>
          </div>
        </nav>
      </header>
      <main className="main">
        {/* Add content sections here, tailored to your app's purpose */}
        <section className="about-us">
          <h2>{words[language].about}</h2>
          <p>
            {/* Add descriptive text about your app */}
          </p>
        </section>
        {/* Add more sections for different functionalities or information */}
      </main>
      <footer className="footer">
        <span>{words[language].history}</span>
        <span>{words[language].feedback}</span>
      </footer>
    </div>
  );
}

export default EngtoThai;
