// EngToThai.js

import React, { useState } from 'react';
import './Engtothai.css';

function ThaiToEng() {
  const [inputText, setInputText] = useState('');
  const [translatedText, setTranslatedText] = useState('');

  const handleTranslate = async () => {
    try {
      const response = await fetch('http://127.0.0.1:5000/translateEng2thai', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ text: inputText }),
      });

      const data = await response.json();
      setTranslatedText(data.translated_text);
    } catch (error) {
      console.error('Error translating text:', error);
    }
  };

  return (
    <div className="container">
      <div className="header">Hello World</div>
      <div className="flags">
        <div className="flag">
        <img src="https://upload.wikimedia.org/wikipedia/commons/a/a9/Flag_of_Thailand.svg" alt="Thai" width="50" />
          <div>Thai</div>
        </div>
        <div className="flag">
          <img src="https://upload.wikimedia.org/wikipedia/en/a/ae/Flag_of_the_United_Kingdom.svg" alt="English" width="50" />
          <div>English</div>
        </div>
      </div>
      <div>
        <textarea
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
          placeholder="Enter text to translate"
        />
        <textarea
          value={translatedText}
          readOnly
          placeholder="Translation will appear here"
        />
      </div>
      <button onClick={handleTranslate}>Translate</button>
      <div className="footer">
        <img src="https://img.icons8.com/material-outlined/24/000000/history.png" alt="History" />
        <img src="https://img.icons8.com/material-outlined/24/000000/feedback.png" alt="Feedback" />
      </div>
    </div>
  );
}

export default ThaiToEng;
