import React from 'react';
import { Container, Box, Typography, Button, IconButton } from '@mui/material';
import { AccessTime, InsertCommentOutlined, SwapHoriz } from '@mui/icons-material';
import { useHistory } from 'react-router-dom';
import './Engtothai.css';

const EngtoThaiWidget = () => {
  const history = useHistory();

  const handleTranslate = () => {
    console.log('Button pressed ...');
  };

  return (
    <Container>
      <Box className="root-container">
        <Box className="styled-box" style={{ top: '20%', left: '10%' }} />
        <Box className="styled-box" style={{ top: '20%', right: '10%' }} />

        <Typography className="styled-typography" style={{ top: '5%', fontSize: 60 }}>
          Hello World
        </Typography>

        <Typography className="styled-typography" style={{ top: '40%', left: '25%', fontSize: 40 }}>
          English
        </Typography>

        <Typography className="styled-typography" style={{ top: '40%', right: '25%', fontSize: 40 }}>
          Thai
        </Typography>

        <IconButton className="icon-button" style={{ top: '40%', left: '50%' }} onClick={() => history.push('/ThaitoEng')}>
          <SwapHoriz fontSize="large" />
        </IconButton>

        <Box
          className="flag-image"
          style={{
            top: '35%',
            left: '15%',
            width: 100,
            height: 50,
            backgroundImage: `url('/assets/images/Flag_of_the_United_Kingdom_(1-2).svg.png')`,
          }}
        />

        <Box
          className="flag-image"
          style={{
            top: '35%',
            right: '15%',
            width: 90,
            height: 52,
            backgroundImage: `url('/assets/images/Flag_of_Thailand.svg.png')`,
          }}
        />

        <Button className="styled-button" variant="contained" color="primary" onClick={handleTranslate} style={{ bottom: '15%' }}>
          Translate
        </Button>

        <Box className="bottom-container">
          <IconButton onClick={() => history.push('/History')}>
            <AccessTime fontSize="large" />
          </IconButton>
          <Typography className="styled-typography" style={{ fontSize: 16 }}>History</Typography>
          <IconButton>
            <InsertCommentOutlined fontSize="large" />
          </IconButton>
          <Typography className="styled-typography" style={{ fontSize: 16 }}>Feedback</Typography>
        </Box>
      </Box>
    </Container>
  );
};

export default EngtoThaiWidget;
